import React, { Component } from "react";
import axios from "axios";
import { Variables } from "../Variable";
import "./AppointmentStatus.css";

export class AppointmentStatus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Appointments: [],
      patientName: "",
      date: "",
      time: "",
      reasonForVisit: "",
      status: "scheduled", // Set default value as "scheduled"
      doctorId: "",
      showModal: false // Track the modal visibility
    };
  }

  componentDidMount() {
    const patientName = localStorage.getItem("patientName");
    if (patientName) {
      this.setState({ patientName: patientName }, () => {
        this.fetchAppointments();
      });
    }
  }

  fetchAppointments = () => {
    const { patientName } = this.state;

    axios
      .get(Variables.API_URL + "Appointments")
      .then((response) => {
        const filteredAppointments = response.data.filter(
          (appointment) => appointment.patientName === patientName
        );
        this.setState({ Appointments: filteredAppointments });
      })
      .catch((error) => {
        console.error("Error", error);
      });
  };

  handleViewStatus = () => {
    this.setState({ showModal: true });
  };

  handleCloseModal = () => {
    this.setState({ showModal: false });
  };

  render() {
    const { Appointments, showModal } = this.state;

    return (
      <div className="card-container">
        {Appointments.map((appointment) => (
          <div key={appointment.appointmentId} className="card">
            <h3>{appointment.patientName}</h3>
            <p>Date: {appointment.date}</p>
            <p>Time: {appointment.time}</p>
            <p>Reason for Visit: {appointment.reasonForVisit}</p>
            <p>Status: {appointment.status}</p>
            <button onClick={this.handleViewStatus}>View Status</button>
          </div>
        ))}

        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <div className="container">
                <ol className="progress-meter">
                  <li
                    className={`progress-point ${
                      Appointments.some(
                        (appointment) => appointment.status === "scheduled" || "Callstat" || "Waitlisted" || "Approved"
                      )
                        ? "done"
                        : "todo"
                    }`}
                  >
                    Scheduled
                  </li>
                  <li
                    className={`progress-point ${
                      Appointments.some(
                        (appointment) => appointment.status === "Callstat" || "scheduled" || "Approved"
                      )
                        ? "done"
                        : "todo"
                    }`}
                  >
                    Call Stat
                  </li>
                  <li
                    className={`progress-point ${
                      Appointments.some(
                        (appointment) => appointment.status === "Waitlisted" || "Approved"
                      )
                        ? "done"
                        : "todo"
                    }`}
                  >
                    Waitlisted
                  </li>
                  <li
                    className={`progress-point ${
                      Appointments.some(
                        (appointment) => appointment.status === "Approved"
                      )
                        ? "done"
                        : "todo"
                    }`}
                  >
                    Approved
                  </li>
                </ol>
              </div>
              <button onClick={this.handleCloseModal}>Close</button>
            </div>
          </div>
        )}
      </div>
    );
  }
}
