import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { toast } from 'react-toastify';
import image1 from './image-1.png';
import image2 from './image-2.png';
import './Register.css';

function Register() {
  const history = useHistory();

  const [scrollDisabled, setScrollDisabled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (scrollDisabled) {
        window.scrollTo(0, 0);
      }
    };

    if (scrollDisabled) {
      document.body.style.overflow = 'hidden';
      window.addEventListener('scroll', handleScroll);
    } else {
      document.body.style.overflow = 'auto';
      window.removeEventListener('scroll', handleScroll);
    }

    return () => {
      document.body.style.overflow = 'auto';
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrollDisabled]);

  const disableScroll = () => {
    setScrollDisabled(true);
  };

  const enableScroll = () => {
    setScrollDisabled(false);
  };

  useEffect(() => {
    disableScroll();
  }, []);

  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [email, setEmail] = useState('');
  const [phone, setPhone] = useState('');
  const [country, setCountry] = useState('India');
  const [address, setAddress] = useState('');
  const [gender, setGender] = useState('');
  const [role, setRole] = useState('');

  const IsValidate = () => {
    let isProceed = true;
    let errorMessage = 'Please enter a value in ';
  
    if (name === '') {
      isProceed = false;
      errorMessage += ' Fullname';
    }
  
    if (role === '') {
      isProceed = false;
      errorMessage += ' Role';
    }
  
    if (password === '') {
      isProceed = false;
      errorMessage += ' Password';
    }
  
    if (email === '') {
      isProceed = false;
      errorMessage += ' Email';
    }
  
    if (!isProceed) {
      toast.warning(errorMessage);
    } else {
      if (!/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email)) {
        isProceed = false;
        toast.warning('Please enter a valid email');
      }
    }
  
    return isProceed;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const regObj = { name, password, email, phone, country, address, gender, role };

    if (IsValidate()) {
      fetch('https://localhost:7109/api/Users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(regObj)
      })
        .then((res) => {
          toast.success('Registered Successfully');
          history.push('/login');
        })
        .catch((err) => {
          toast.error('Failed: ' + err.message);
        });
    }
  };

  return (
    <div className="wrapper">
      <div className="inner">
        <img src={image1} alt="" className="image-1" />
        <form className="form" onSubmit={handleSubmit}>
          <h3>New Account?</h3>
          <div className="form-holder">
            <span className="lnr lnr-user" />
            <input
              type="text"
              className="form-control"
              placeholder="Username"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          {/* Rest of the form inputs */}
          <button type="submit">
            <span>Register</span>
          </button>
        </form>
        <img src={image2} alt="" className="image-2" />
      </div>
    </div>
  );
}

export default Register;
