import React from 'react';
import './Sidebar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTachometerAlt, faUsers, faBox, faCog } from '@fortawesome/free-solid-svg-icons';

const Sidebar = () => {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Users')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };
  return (
    <div className="sidebar">
      <ul>
        <li>
          <button className="nav-button active">
            <FontAwesomeIcon icon={faTachometerAlt} className="nav-icon" />
            Doctor
          </button>
        </li>
        <li>
          <button className="nav-button">
            <FontAwesomeIcon icon={faUsers} className="nav-icon" />
            Patients
          </button>
        </li>
        <li>
          <button className="nav-button">
            <FontAwesomeIcon icon={faBox} className="nav-icon" />
            Appointments
          </button>
        </li>
        <li>
          <button className="nav-button">
            <FontAwesomeIcon icon={faCog} className="nav-icon" />
            Settings
          </button>
        </li>
        {/* Add more menu items as needed */}
      </ul>
      <div className="approval-container">
      {registers.map((register, index) => (
        <div key={register.id} className={`card ${index % 4 === 0 ? 'first-card' : ''}`}>
          <p>Name: {register.name}</p>
          <p>Role: {register.role}</p>
          <p>Specialization: {register.specialization}</p>
          <p>Gender: {register.gender}</p>
          <p>Status: {register.status}</p>
          {register.status === 'approved' && (
          <Link to="/login" className="access-button">Access</Link>
          )}
        </div>
      ))}
    </div>
    </div>
  );
};

export default Sidebar;
