import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Variables } from '../Variable';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Approval.css';

function AdminApproval() {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Users')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };

  const handleAccept = (id) => {
    const updatedRegisters = registers.map((register) => {
      if (register.id === id) {
        return { ...register, status: 'approved' };
      }
      return register;
    });
    setRegisters(updatedRegisters);
    updateStatusInDatabase(id, 'approved');
  };

  const handleReject = (id) => {
    const updatedRegisters = registers.map((register) => {
      if (register.id === id) {
        console.log
        return { ...register, status: 'declined' };
      }
      return register;
    });
    setRegisters(updatedRegisters);
    updateStatusInDatabase(id, 'declined');
  };
  const updateStatusInDatabase = (id, status) => {
    axios
      .put(Variables.API_URL + `Users/${id}`, { status })
      .then((response) => {
        toast.success('Status updated successfully');
      })
      .catch((error) => {
        console.error('Error updating status:', error);
        console.error('Response:', error.response); // Log the response object
        toast.error('Error updating status:', error.message);
      });
  };
  

  return (
    <div className="approval-container">
      {registers.map((register, index) => (
        <div key={register.id} className={`card ${index % 4 === 0 ? 'first-card' : ''}`}>
          <p>Name: {register.name}</p>
          <p>Role: {register.role}</p>
          <p>Specialization: {register.specialization}</p>
          <p>Gender: {register.gender}</p>
          <p>Status: {register.status}</p>
          <div className="button-container">
            <button className="accept-button" onClick={() => handleAccept(register.id)}>
              Accept
            </button>
            <button className="reject-button" onClick={() => handleReject(register.id)}>
              Reject
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default AdminApproval;
