import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Variables } from '../Variable';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom';
import './Approval.css';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

function Approval() {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Users')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };

  return (
    <div className="container">
      <div className="row">
        {registers.map((register, index) => (
          <div key={register.id} className="col-lg-3 col-md-6">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{register.name}</h5>
                <p className="card-text">Role: {register.role}</p>
                <p className="card-text">Specialization: {register.specialization}</p>
                <p className="card-text">Gender: {register.gender}</p>
                <p className="card-text">Status: {register.status}</p>
                {register.status === 'approved' && (
                  <Link to="/login" className="btn btn-primary">Access</Link>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Approval;
