import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Variables } from '../Variable';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { Link } from 'react-router-dom';
import './Approval.css';

function Approval() {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Users')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };

  return (
    <div className="approval-container">
      <div className="column"></div>
      <div className="column">
        {registers.map((register, index) => {
          if (index % 3 === 0) {
            return (
              <div key={register.id} className="card">
                <p>Name: {register.name}</p>
                <p>Role: {register.role}</p>
                <p>Specialization: {register.specialization}</p>
                <p>Gender: {register.gender}</p>
                <p>Status: {register.status}</p>
                {register.status === 'approved' && (
                  <Link to="/login" className="access-button">Access</Link>
                )}
              </div>
            );
          }
          return null;
        })}
      </div>
      <div className="column">
        {registers.map((register, index) => {
          if (index % 3 === 1) {
            return (
              <div key={register.id} className="card">
                <p>Name: {register.name}</p>
                <p>Role: {register.role}</p>
                <p>Specialization: {register.specialization}</p>
                <p>Gender: {register.gender}</p>
                <p>Status: {register.status}</p>
                {register.status === 'approved' && (
                  <Link to="/login" className="access-button">Access</Link>
                )}
              </div>
            );
          }
          return null;
        })}
      </div>
      <div className="column">
        {registers.map((register, index) => {
          if (index % 3 === 2) {
            return (
              <div key={register.id} className="card">
                <p>Name: {register.name}</p>
                <p>Role: {register.role}</p>
                <p>Specialization: {register.specialization}</p>
                <p>Gender: {register.gender}</p>
                <p>Status: {register.status}</p>
                {register.status === 'approved' && (
                  <Link to="/login" className="access-button">Access</Link>
                )}
              </div>
            );
          }
          return null;
        })}
      </div>
    </div>
  );
}

export default Approval;
