import React, { Component } from "react";
import { Variables } from '../Variable';
import "bootstrap/dist/css/bootstrap.min.css"; // Import Bootstrap CSS
import axios from "axios";

class DoctorPost extends Component {
  constructor(props) {
    super(props);
    this.state = {
      doctorName: "",
      imageFile: null,
      gender: "",
      phone: "",
      email: "",
      experience: 0,
      licenseNumber: "",
      tesimonials: ""
    };
  }

  handleInputChange = (event) => {
    this.setState({ [event.target.name]: event.target.value });
  };

  handleImageChange = (event) => {
    this.setState({ imageFile: event.target.files[0] });
  };

  handleSubmit = (event) => {
    event.preventDefault();
    // Send a POST request to create a new doctor
    this.createDoctor();
  };

  handleUpdate = () => {
    // Send a PUT request to update the doctor
    const { doctorId } = this.props.match.params;
    this.updateDoctor(doctorId);
  };

  componentDidMount() {
    // Fetch the doctor data to pre-fill the form
    const { doctorId } = this.props.match.params;
    this.fetchDoctor(doctorId);
  }

  fetchDoctor = (doctorId) => {
    axios
      .get(Variables.API_URL + `Doctors/${doctorId}`)
      .then((response) => {
        const doctor = response.data;
        this.setState({
          doctorName: doctor.doctorName,
          gender: doctor.gender,
          phone: doctor.phone,
          email: doctor.email,
          experience: doctor.experience,
          licenseNumber: doctor.licenseNumber,
          tesimonials: doctor.tesimonials,
        });
      })
      .catch((error) => {
        console.error("Error", error);
        // Handle the error appropriately
      });
  };

  createDoctor = () => {
    const formData = new FormData();
    formData.append("doctorName", this.state.doctorName);
    formData.append("imageFile", this.state.imageFile);
    formData.append("gender", this.state.gender);
    formData.append("phone", this.state.phone);
    formData.append("email", this.state.email);
    formData.append("experience", this.state.experience);
    formData.append("licenseNumber", this.state.licenseNumber);
    formData.append("tesimonials", this.state.tesimonials);

    axios
      .post(Variables.API_URL + "Doctors", formData)
      .then((response) => {
        console.log("Doctor created:", response.data);
        // Perform any necessary actions after successful creation
      })
      .catch((error) => {
        console.error("Error", error);
        // Handle the error appropriately
      });
  };

  updateDoctor = (doctorId) => {
    const formData = new FormData();
    formData.append("doctorName", this.state.doctorName);
    formData.append("imageFile", this.state.imageFile);
    formData.append("gender", this.state.gender);
    formData.append("phone", this.state.phone);
    formData.append("email", this.state.email);
    formData.append("experience", this.state.experience);
    formData.append("licenseNumber", this.state.licenseNumber);
    formData.append("tesimonials", this.state.tesimonials);

    axios
      .put(Variables.API_URL + `Doctors/${doctorId}`, formData)
      .then((response) => {
        console.log("Doctor updated:", response.data);
        // Perform any necessary actions after successful update
      })
      .catch((error) => {
        console.error("Error", error);
        // Handle the error appropriately
      });
  };

  render() {
    return (
      <div className="container">
        <br />
        <h2>Doctor Form</h2>
        <form onSubmit={this.handleSubmit}>
          <div className="form-group">
            <label>Doctor Name</label>
            <input
              type="text"
              name="doctorName"
              className="form-control"
              value={this.state.doctorName}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Gender</label>
            <input
              type="text"
              name="gender"
              className="form-control"
              value={this.state.gender}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Phone</label>
            <input
              type="text"
              name="phone"
              className="form-control"
              value={this.state.phone}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              name="email"
              className="form-control"
              value={this.state.email}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Experience</label>
            <input
              type="number"
              name="experience"
              className="form-control"
              value={this.state.experience}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>License Number</label>
            <input
              type="text"
              name="licenseNumber"
              className="form-control"
              value={this.state.licenseNumber}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Tesimonials</label>
            <input
              type="text"
              name="tesimonials"
              className="form-control"
              value={this.state.tesimonials}
              onChange={this.handleInputChange}
              required
            />
          </div>
          <div className="form-group">
            <label>Image</label>
            <input
              type="file"
              name="imageFile"
              className="form-control-file"
              onChange={this.handleImageChange}
              required
            />
          </div>
          <div className="d-flex justify-content-between">
            <button type="submit" className="btn btn-primary">
              Create
            </button>
            <button
              className="btn btn-primary"
              onClick={this.handleUpdate}
            >
              Update
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default DoctorP;
