import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Variables } from '../Variable';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Approval.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function AdminApproval() {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Users')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };

  const handleAccept = (registerId, currentStatus) => {
    const updatedStatus = 'approved';

    // Assign the fields to variables
    const { id, name, email, password, role, phone, country, address, gender, specialization } = registers.find(
      (register) => register.id === registerId
    );

    // Update the status in the database
    axios
      .put(Variables.API_URL + `Users/${registerId}`, {
        id,
        name,
        email,
        password,
        role,
        phone,
        country,
        address,
        gender,
        specialization,
        status: updatedStatus,
      })
      .then((response) => {
        // Handle successful update
        console.log('Status updated successfully');
        // You can also update the local state if needed
        // Example: Update the status locally
        const updatedRegisters = registers.map((register) => {
          if (register.id === registerId) {
            return { ...register, status: updatedStatus };
          }
          return register;
        });
        setRegisters(updatedRegisters);
      })
      .catch((error) => {
        console.error('Error updating status:', error);
        toast.error('Error updating status:', error.message);
      });
  };

  const handleReject = (registerId, currentStatus) => {
    const updatedStatus = 'rejected';

    // Assign the fields to variables
    const { id, name, email, password, role, phone, country, address, gender, specialization } = registers.find(
      (register) => register.id === registerId
    );

    // Update the status in the database
    axios
      .put(Variables.API_URL + `Users/${registerId}`, {
        id,
        name,
        email,
        password,
        role,
        phone,
        country,
        address,
        gender,
        specialization,
        status: updatedStatus,
      })
      .then((response) => {
        // Handle successful update
        console.log('Status updated successfully');
        // You can also update the local state if needed
        // Example: Update the status locally
        const updatedRegisters = registers.map((register) => {
          if (register.id === registerId) {
            return { ...register, status: updatedStatus };
          }
          return register;
        });
        setRegisters(updatedRegisters);
      })
      .catch((error) => {
        console.error('Error updating status:', error);
        toast.error('Error updating status:', error.message);
      });
  };

  return (
    <div className="approval-container">
      <Row className="pt-6">
        {registers.map((register, index) => (
          <Col key={register.id} sm={4}>
            <Card className={`card ${index % 4 === 0 ? 'first-card' : ''}`}>
              <Card.Body>
                <Card.Title>Name: {register.name}</Card.Title>
                <Card.Text>Role: {register.role}</Card.Text>
                <Card.Text>Specialization: {register.specialization}</Card.Text>
                <Card.Text>Gender: {register.gender}</Card.Text>
                <Card.Text>Status: {register.status}</Card.Text>
                <div className="button-container">
                  <Button className="accept-button" onClick={() => handleAccept(register.id)}>
                    Accept
                  </Button>&nbsp;
                  <Button className="reject-button" onClick={() => handleReject(register.id)}>
                    Reject
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
}

export default AdminApproval;
