import React from 'react';
import './OtpVerify.css';

const O = () => {
  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission
  };

  const digitValidate = (target) => {
    // Perform digit validation logic
  };

  const tabChange = (index, e) => {
    // Handle tab change logic
  };

  return (
    <div className="mobile-container">
      <div className="mobile-screen">
        <form onSubmit={handleSubmit} className="otp-form">
          <div className="otpbox">
            <input
              className="otp"
              type="text"
              onInput={(e) => digitValidate(e.target)}
              onKeyUp={(e) => tabChange(1, e)}
              maxLength="1"
            />
            <input
              className="otp"
              type="text"
              onInput={(e) => digitValidate(e.target)}
              onKeyUp={(e) => tabChange(2, e)}
              maxLength="1"
            />
            <input
              className="otp"
              type="text"
              onInput={(e) => digitValidate(e.target)}
              onKeyUp={(e) => tabChange(3, e)}
              maxLength="1"
            />
            <input
              className="otp"
              type="text"
              onInput={(e) => digitValidate(e.target)}
              onKeyUp={(e) => tabChange(4, e)}
              maxLength="1"
            />

            <hr className="mt-4" />
            <button type="submit" className="customBtn">
              Verify
            </button>
          </div>
        </form>
      </div>
      <div className="home-button">
        <button>Home</button>
      </div>
    </div>
  );
};

export default MobilePhone;
