import React, { Component } from "react";
import axios from "axios";
import { Variables } from "../Variable";
import "./AppointmentStatus.css";

export class AppointmentStatus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Appointments: [],
      patientName: "",
      date: "",
      time: "",
      reasonForVisit: "",
      status: "scheduled", // Set default value as "scheduled"
      doctorId: ""
    };
  }

  componentDidMount() {
    const patientName = localStorage.getItem("patientName");
    if (patientName) {
      this.setState({ patientName: patientName }, () => {
        this.fetchAppointments();
      });
    }
  }

  fetchAppointments = () => {
    const { patientName } = this.state;

    axios
      .get(Variables.API_URL + "Appointments", {
        params: {
          patientName: patientName
        }
      })
      .then((response) => {
        this.setState({ Appointments: response.data }, () => {
          this.setStatus();
        });
      })
      .catch((error) => {
        console.error("Error", error);
      });
  };

  setStatus = () => {
    const { Appointments, patientName } = this.state;
    const scheduled = Appointments.find(
      (appointment) =>
        appointment.patientName === patientName && appointment.status === "scheduled"
    );
  
    if (scheduled) {
      this.setState({ status: "scheduled" });
    } else {
      this.setState({ status: "" });
    }
  };
  

  render() {
    const { status } = this.state;

    return (
      <div className="circle-container">
        <div
          className={`circle ${
            status === "scheduled" ? "green-circle" : ""
          }`}
        >
          <span className="circle-label">Scheduled</span>
        </div>
        <div className="circle">
          <span className="circle-label">Call Stat</span>
        </div>
        <div className="circle">
          <span className="circle-label">Waitlisted</span>
        </div>
        <div className="circle">
          <span className="circle-label">Approved</span>
        </div>
      </div>
    );
  }
}
