import React, { Component } from "react";
import axios from "axios";
import { Variables } from "../Variable";
import "./AppointmentStatus.css";

export class AppointmentStatus extends Component {
  constructor(props) {
    super(props);
    this.state = {
      Appointments: [],
      patientName: "",
      date: "",
      time: "",
      reasonForVisit: "",
      status: "scheduled", // Set default value as "scheduled"
      doctorId: ""
    };
  }

  componentDidMount() {
    const patientName = localStorage.getItem("patientName");
    if (patientName) {
      this.setState({ patientName: patientName }, () => {
        this.fetchAppointments();
      });
    }
  }

  fetchAppointments = () => {
    const { patientName } = this.state;

    axios
      .get(Variables.API_URL + "Appointments", {
        params: {
          patientName: patientName
        }
      })
      .then((response) => {
        this.setState({ Appointments: response.data }, () => {
          this.setStatus();
        });
      })
      .catch((error) => {
        console.error("Error", error);
      });
  };

  setStatus = () => {
    const { Appointments } = this.state;
    const scheduled = Appointments.find(
      (appointment) => appointment.status === "scheduled"
    );
    const callStat = Appointments.find(
      (appointment) => appointment.status === "Callstat"
    );
    const waitlisted = Appointments.find(
      (appointment) => appointment.status === "Waitlisted"
    );

    if (scheduled && callStat && waitlisted) {
      this.setState({ status: "Waitlisted" });
    } else if (scheduled && callStat) {
      this.setState({ status: "Callstat" });
    } else if (scheduled) {
      this.setState({ status: "scheduled" });
    }
  };

  render() {
    const { status } = this.state;

    return (
      <div className="circle-container">
        <div
          className={`circle ${
            status === "scheduled" || status === "Callstat" || status === "Waitlisted"
              ? "green-circle"
              : ""
          }`}
        >
          <span className="circle-label">Scheduled</span>
        </div>
        <div
          className={`circle ${
            status === "Callstat" || status === "Waitlisted" ? "green-circle" : ""
          }`}
        >
          <span className="circle-label">Call Stat</span>
        </div>
        <div className={`circle ${status === "Waitlisted" ? "green-circle" : ""}`}>
          <span className="circle-label">Waitlisted</span>
        </div>
        <div
          className={`circle ${
            status === "scheduled" || status === "Callstat" ? "green-circle" : ""
          }`}
        >
          <span className="circle-label">Approved</span>
        </div>
      </div>
    );
  }
}
