import React, { useState, useEffect } from "react";
import { Variables } from '../Variable';
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.css';
export default function Doctor() {
  const [Courses, setCourses] = useState([]);
  const [courseId, setCourseId] = useState(0);
  const [courseName, setCourseName] = useState("");
  const [courseDescription, setCourseDescription] = useState("");
  const [courseType, setCourseType] = useState("");
  const [courseDuration, setCourseDuration] = useState("");
  const [courseEnrollment, setCourseEnrollment] = useState("");
  const [selectedCourse, setSelectedCourse] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    // Check if the user is authenticated
    const isAuthenticated = getCookieValue('token');;
    if (!isAuthenticated) {
      navigate('/doctor'); // Redirect to the login page if not authenticated
    } else {
      fetchItems(); 
      const token = getCookieValue('token');
     // Fetch the token from cookies "token"   const token =
    }
  }, [navigate]);

  const getCookieValue = (name) => {
    const cookies = document.cookie.split(';');
    for (let i = 0; i < cookies.length; i++) {
      const cookie = cookies[i].trim();
      if (cookie.startsWith(`${name}=`)) {
        return cookie.substring(name.length + 1);
      }
    }
    return null;
  };

  const fetchItems = () => {
  axios
    .get(Variables.API_URL + 'Doctors', {
      headers: {
        Authorization: `Bearer ${getCookieValue('token')}`,
      },
    })
    .then((response) => {
      if (response.status === 200) {
        setCourses(response.data);
      } else {
        throw new Error('Failed to fetch courses');
      }
    })
    .catch((error) => {
      console.error('Error fetching courses:', error);
      toast.error('Error fetching courses:', error.message);
    });
};





  return (
    <div className="container-fluid">
      <h1>Doctor</h1>

      <div className="table-responsive mt-3">
        <table className="table table-hover">
          <thead className="table-dark text-center">
            <tr>
              <th>Course Id</th>
              <th>Course Name</th>
              <th>Course Description</th>
              <th>Course Type</th>
              <th>Course Duration</th>
              <th>Course Enrollment</th>
              <th>CRUD</th>
            </tr>
          </thead>
          <tbody className="text-center">
            {Courses.map((course) => (
              <tr key={course.courseId}>
                <td>{course.courseId}</td>
                <td>{course.courseName}</td>
                <td>{course.courseDescription}</td>
                <td>{course.courseType}</td>
                <td>{course.courseDuration}</td>
                <td>{course.courseEnrollment}</td>
              
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}
