import React, { useState, useEffect } from 'react'
import image1 from './image-1.png'
import image2 from './image-2.png'
import './Register.css'
import { toast } from "react-toastify";

function Register() {
	const [scrollDisabled, setScrollDisabled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (scrollDisabled) {
        window.scrollTo(0, 0);
      }
    };

    if (scrollDisabled) {
      document.body.style.overflow = 'hidden';
      window.addEventListener('scroll', handleScroll);
    } else {
      document.body.style.overflow = 'auto';
      window.removeEventListener('scroll', handleScroll);
    }
	const [name, namechange] = useState("");
    const [password, passwordchange] = useState("");
    const [email, emailchange] = useState("");
    const [phone, phonechange] = useState("");
    const [country, countrychange] = useState("India");
    const [address, addresschange] = useState("");
    const [gender, genderchange] = useState("");
    const [role,rolechange]=useState("");
    const navigate = useNavigate();

    const IsValidate = () => {
        let isproceed = true;
        let errormessage = 'Please enter the value in ';
        // if (id === null || id === '') {
        //     isproceed = false;
        //     errormessage += ' Username';
        // }
        if (name === null || name === '') {
            isproceed = false;
            errormessage += ' Fullname';
        }
        if (role === null || role === '') {
            isproceed = false;
            errormessage += ' Role';
        }
        if (password === null || password === '') {
            isproceed = false;
            errormessage += ' Password';
        }
        if (email === null || email === '') {
            isproceed = false;
            errormessage += ' Email';
        }
      
        if (!isproceed) {
            toast.warning(errormessage)
        } else {
            if (/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email)) {

            } else {
                isproceed = false;
                toast.warning('Please enter the valid email')
            }
        }
        return isproceed;
    }

    const handlesubmit = (e) => {
        e.preventDefault();
        let regobj = { name, password, email, phone, country, address, gender,role }
        console.log(JSON.stringify(regobj))
        if (IsValidate()) {
            // console.log(regobj);
            fetch("https://localhost:7109/api/Users", {
                method: "POST",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(regobj)
            }).then((res) => {
                toast.success('Registered Sucessfully')
                navigate('/login')
            }).catch((err) => {
                toast.error('Failed:' + err.message);
            });
        }
    }
    return () => {
      document.body.style.overflow = 'auto';
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrollDisabled]);

  const disableScroll = () => {
    setScrollDisabled(true);
  };

  const enableScroll = () => {
    setScrollDisabled(false);
  };

  useEffect(() => {
	disableScroll();
  })
  
  return (
    <div className="wrapper">
			<div className="inner">
				<img src={image1} alt="" className="image-1" />
				<form action="" className='form'>
					 <h3>New Account?</h3>
					<div className="form-holder">
						<span className="lnr lnr-user"></span>
						<input type="text" className="form-control" placeholder="Username" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-phone-handset"></span>
						<input type="text" className="form-control" placeholder="Phone Number" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-envelope"></span>
						<input type="text" className="form-control" placeholder="Mail" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-lock"></span>
						<input type="password" className="form-control" placeholder="Password" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-lock"></span>
						<input type="password" className="form-control" placeholder="Confirm Password" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-lock"></span>
						<input type="password" className="form-control" placeholder="Confirm Password" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-lock"></span>
						<input type="password" className="form-control" placeholder="Confirm Password" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-lock"></span>
						<input type="password" className="form-control" placeholder="Confirm Password" />
					</div>
					<div className="form-holder">
						<span className="lnr lnr-lock"></span>
						<input type="password" className="form-control" placeholder="Confirm Password" />
					</div>
					<button>
						<span>Register</span>
					</button>
				</form>
				<img src={image2} alt="" className="image-2" />
			</div>
			
		</div>
  )
}

export default Register
