import React, { useState, useEffect } from 'react'
import image1 from './image-1.png'
import image2 from './image-2.png'
import './Register.css'
import { toast } from "react-toastify";
import { Link, useNavigate } from "react-router-dom";

function Login() {
	   const navigate = useNavigate();

 const [scrollDisabled, setScrollDisabled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (scrollDisabled) {
        window.scrollTo(0, 0);
      }
    };

    if (scrollDisabled) {
      document.body.style.overflow = 'hidden';
      window.addEventListener('scroll', handleScroll);
    } else {
      document.body.style.overflow = 'auto';
      window.removeEventListener('scroll', handleScroll);
    }
    return () => {
      document.body.style.overflow = 'auto';
      window.removeEventListener('scroll', handleScroll);
    };
  }, [scrollDisabled]);

  const disableScroll = () => {
    setScrollDisabled(true);
  };

  const enableScroll = () => {
    setScrollDisabled(false);
  };

  useEffect(() => {
	disableScroll();
  })
  

  const [name, namechange] = useState("");
  const [password, passwordchange] = useState("");
  const [email, emailchange] = useState("");
  const [phone, phonechange] = useState("");
  const [country, countrychange] = useState("India");
  const [address, addresschange] = useState("");
  const [gender, genderchange] = useState("");
  const [role,rolechange]=useState("");

  const IsValidate = () => {
	  let isproceed = true;
	  let errormessage = 'Please enter the value in ';
	  // if (id === null || id === '') {
	  //     isproceed = false;
	  //     errormessage += ' Username';
	  // }
	  if (name === null || name === '') {
		  isproceed = false;
		  errormessage += ' Fullname';
	  }
	  if (role === null || role === '') {
		  isproceed = false;
		  errormessage += ' Role';
	  }
	  if (password === null || password === '') {
		  isproceed = false;
		  errormessage += ' Password';
	  }
	  if (email === null || email === '') {
		  isproceed = false;
		  errormessage += ' Email';
	  }
	
	  if (!isproceed) {
		  toast.warning(errormessage)
	  } else {
		  if (/^[a-zA-Z0-9]+@[a-zA-Z0-9]+\.[A-Za-z]+$/.test(email)) {

		  } else {
			  isproceed = false;
			  toast.warning('Please enter the valid email')
		  }
	  }
	  return isproceed;
  }

  const handlesubmit = (e) => {
	  e.preventDefault();
	  let regobj = { name, password, email, phone, country, address, gender,role }
	  console.log(JSON.stringify(regobj))
	  if (IsValidate()) {
		  // console.log(regobj);
		  fetch("https://localhost:7224/api/Users", {
			  method: "POST",
			  headers: { 'Content-Type': 'application/json' },
			  body: JSON.stringify(regobj)
		  }).then((res) => {
			  toast.success('Registered Sucessfully')
			  navigate('/login')
		  }).catch((err) => {
			  toast.error('Failed:' + err.message);
		  });
	  }
  }
  return (
    <div className="wrapper">
			<div className="inner">
				<img src={image1} alt="" className="image-1" />
				<form  onSubmit={handlesubmit} className='form'>
					 <h3>New Account?</h3>
					 <div className="col-lg-6">
                                    <div className="form-group">
                                        <label>Full Name <span className="errmsg">*</span></label>
                                        <input value={name} onChange={e => namechange(e.target.value)} className="form-control"></input>
                                    </div>
                                </div>
                               
								<div className="col-lg-6">
                                    <div className="form-group">
                                        <label>Password <span className="errmsg">*</span></label>
                                        <input value={password} onChange={e => passwordchange(e.target.value)} type="password" className="form-control"></input>
                                    </div>
                                </div>
                                <div className="form-group">
                <div className="form-check">
                  <input
                    type="radio"
                    name="role"
                    id="userRole"
                    value="user"
                    checked={role === 'user'}
                    onChange={(e) => setRole(e.target.value)}
                    className="form-check-input"
                  />
                  <label htmlFor="userRole" className="form-check-label">User</label>
                </div>
                <div className="form-check">
                  <input
                    type="radio"
                    name="role"
                    id="adminRole"
                    value="admin"
                    checked={role === 'admin'}
                    onChange={(e) => setRole(e.target.value)}
                    className="form-check-input"
                  />
                  <label htmlFor="adminRole" className="form-check-label">Admin</label>
                </div>
              </div>
								<div className="card-footer">
                            <button type="submit" className="btn btn-primary">Register</button> 
							<button>  <Link to={'/login'} className="btn btn-primary">Close</Link></button> 
                        </div>
					
				</form>
				<img src={image2} alt="" className="image-2" />
			</div>
			
		</div>
  )
}

export default Login
