import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Variables } from '../Variable';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Approval.css';
function AppointmentApproval() {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Appointments')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };

  const handleAccept = (registerId, currentStatus) => {
    const updatedStatus = 'approved';
  
    // Assign the fields to variables
    const { id, patientName,date,time ,reasonForVisit, status } = registers.find(register => register.id === registerId);
  
    // Update the status in the database
    axios
      .put(Variables.API_URL + `Users/${registerId}`, { id, patientName,date,time ,reasonForVisit, status: updatedStatus })
      .then((response) => {
        // Handle successful update
        console.log('Status updated successfully');
        // You can also update the local state if needed
        // Example: Update the status locally
        const updatedRegisters = registers.map((register) => {
          if (register.id === registerId) {
            return { ...register, status: updatedStatus };
          }
          return register;
        });
        setRegisters(updatedRegisters);
      })
      .catch((error) => {
        console.error('Error updating status:', error);
        toast.error('Error updating status:', error.message);
      });
  };

  const handleReject = (registerId, currentStatus) => {
    const updatedStatus = 'rejected';
  
    // Assign the fields to variables
    const { id, name, email, password, role, phone, country, address, gender, specialization } = registers.find(register => register.id === registerId);
  
    // Update the status in the database
    axios
      .put(Variables.API_URL + `Users/${registerId}`, { id, name, email, password, role, phone, country, address, gender, specialization, status: updatedStatus })
      .then((response) => {
        // Handle successful update
        console.log('Status updated successfully');
        // You can also update the local state if needed
        // Example: Update the status locally
        const updatedRegisters = registers.map((register) => {
          if (register.id === registerId) {
            return { ...register, status: updatedStatus };
          }
          return register;
        });
        setRegisters(updatedRegisters);
      })
      .catch((error) => {
        console.error('Error updating status:', error);
        toast.error('Error updating status:', error.message);
      });
  };
  
  

  return (
    <div className="approval-container">
      {registers.map((register, index) => (
        <div key={register.id} className={`card ${index % 4 === 0 ? 'first-card' : ''}`}>
          <p>Name: {register.patientName}</p>
          <p>Role: {register.date}</p>
          <p>Specialization: {register.time}</p>
          <p>Gender: {register.reasonForVisit}</p>
          <p>Status: {register.status}</p>
          <div className="button-container">
          <button className="accept-button" onClick={() => handleAccept(register.id)}>Accept</button>&nbsp;
          <button className="reject-button" onClick={() => handleReject(register.id)}>Reject</button>
        </div>
        </div>
      ))}
    </div>
  );
}

export default AppointmentApproval
