import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Variables } from '../Variable';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './Approval.css';
function AppointmentApproval() {
  const [registers, setRegisters] = useState([]);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios
      .get(Variables.API_URL + 'Appointments')
      .then((response) => {
        if (response.status === 200) {
          setRegisters(response.data);
        } else {
          throw new Error('Failed to fetch registers');
        }
      })
      .catch((error) => {
        console.error('Error fetching registers:', error);
        toast.error('Error fetching registers:', error.message);
      });
  };


 

  return (
    <div className="approval-container">
      {registers.map((register, index) => (
        <div key={register.id} className={`card ${index % 4 === 0 ? 'first-card' : ''}`}>
          <p>Name: {register.patientName}</p>
          <p>Role: {register.date}</p>
          <p>Specialization: {register.time}</p>
          <p>Gender: {register.reasonForVisit}</p>
          <p>Status: {register.status}</p>
          <div className="button-container">
          <button className="accept-button" onClick={() => handleAccept(register.id)}>Accept</button>&nbsp;
          <button className="reject-button" onClick={() => handleReject(register.id)}>Reject</button>
        </div>
        </div>
      ))}
    </div>
  );
}

export default AppointmentApproval
